mapper.select:
/* 검색 쿼리 실행 */ SELECT RNUM, HELP_ID, HELP_TITLE, HELP_CARE_TYPE, HELP_ADDRESS, HELP_POINT, HELP_PRICE, HELP_CARE_DATE, HELP_CREATED_AT, HELP_START_TIME, HELP_END_TIME, IS_ACCEPTED FROM ( SELECT ROWNUM AS RNUM, HELP_ID, HELP_TITLE, HELP_CARE_TYPE, HELP_ADDRESS, HELP_POINT, HELP_PRICE, HELP_CARE_DATE, HELP_CREATED_AT, HELP_START_TIME, HELP_END_TIME, IS_ACCEPTED FROM ( SELECT HB.HELP_ID, HB.HELP_TITLE, HB.HELP_CARE_TYPE, HB.HELP_ADDRESS, HB.HELP_POINT, HB.HELP_PRICE, HB.HELP_CARE_DATE, HB.HELP_CREATED_AT, HB.HELP_START_TIME, HB.HELP_END_TIME, CASE WHEN EXISTS( SELECT 1 FROM HELP_OFFER HO WHERE HO.HELP_ID = HB.HELP_ID AND HELP_OFFER_STATUS = 'ACCEPT' ) THEN 1 ELSE 0 END AS IS_ACCEPTED FROM HELP_BOARD HB
AND HELP_ADDRESS LIKE '%' || #{cond.sido} || '%'
AND HELP_ADDRESS LIKE '%' || #{cond.gugun} || '%'
AND HELP_CARE_DATE = #{cond.careDate}
AND HELP_CARE_TYPE = #{cond.careType}
AND (HELP_TITLE LIKE '%' || #{cond.keyword} || '%' OR HELP_SPECIAL_NOTE LIKE '%' || #{cond.keyword} || '%')
ORDER BY HELP_CREATED_AT DESC )
WHERE ROWNUM <= #{page.page} * #{page.size}
) WHERE RNUM > (#{page.page} - 1) * #{page.size}

SELECT RNUM, HELP_ID, HELP_TITLE, HELP_CARE_TYPE, HELP_ADDRESS, HELP_POINT, HELP_PRICE, HELP_CARE_DATE, HELP_START_TIME, HELP_END_TIME FROM ( SELECT ROWNUM AS RNUM, HELP_ID, HELP_TITLE, HELP_CARE_TYPE, HELP_ADDRESS, HELP_POINT, HELP_PRICE, HELP_CARE_DATE, HELP_START_TIME, HELP_END_TIME FROM ( SELECT H.HELP_ID, H.HELP_TITLE, H.HELP_CARE_TYPE, H.HELP_ADDRESS, H.HELP_POINT, H.HELP_PRICE, H.HELP_CARE_DATE, H.HELP_START_TIME, H.HELP_END_TIME FROM HELP_BOARD H ORDER BY H.HELP_CREATED_AT DESC )
WHERE ROWNUM <= #{page} * #{size}
)
WHERE RNUM > (#{page} - 1) * #{size}

mapper.delete:
DELETE FROM HELP_BOARD
WHERE HELP_ID = #{helpId}
AND USERS_ID=#{usersId}
