mapper.select:
/* 검색 쿼리 실행 */ SELECT HELP_ID, HELP_TITLE, HELP_CARE_TYPE, HELP_ADDRESS, HELP_POINT, HELP_PRICE, HELP_CARE_DATE, HELP_CREATED_AT FROM HELP_BOARD
AND HELP_ADDRESS LIKE '%' || #{sido} || '%'
AND HELP_ADDRESS LIKE '%' || #{gugun} || '%'
AND HELP_CARE_DATE = #{careDate}
AND HELP_CARE_TYPE = #{careType}
AND (HELP_TITLE LIKE '%' || #{keyword} || '%' OR HELP_SPECIAL_NOTE LIKE '%' || #{keyword} || '%')
ORDER BY HELP_CREATED_AT DESC
