mapper.update:
UPDATE ZIP_BOARD
SET ZIP_VIEWCOUNT = ZIP_VIEWCOUNT + 1
WHERE ZIP_ID = #{zipId}

UPDATE ZIP_BOARD
SET ZIP_PET_CATEGORY = #{zipPetCategory}, ZIP_TITLE = #{zipTitle}, ZIP_CONTENT = #{zipContent}, ZIP_UPDATE_AT = SYSDATE
WHERE ZIP_ID = #{zipId}

mapper.select:
SELECT RNUM, ZIP_ID, ZIP_PET_CATEGORY, ZIP_TITLE, ZIP_CONTENT, ZIP_UPDATE_AT AS zipUpdatedAt , USERS_ID, NICKNAME, ZIP_VIEWCOUNT, ANSWER_COUNT, LIKE_COUNT FROM ( SELECT ROWNUM AS RNUM, ZIP_ID, ZIP_PET_CATEGORY, ZIP_TITLE, ZIP_CONTENT, ZIP_UPDATE_AT, ZIP_VIEWCOUNT, USERS_ID, NICKNAME, ANSWER_COUNT, LIKE_COUNT FROM ( SELECT ZB.ZIP_ID, ZB.ZIP_PET_CATEGORY, ZB.ZIP_TITLE, ZB.ZIP_CONTENT, ZB.ZIP_UPDATE_AT, ZB.ZIP_VIEWCOUNT, U.USERS_ID, U.NICKNAME, (SELECT COUNT(*) FROM ZIP_ANSWER ZA WHERE ZA.ZIP_ID = ZB.ZIP_ID) AS ANSWER_COUNT, (SELECT COUNT(*) FROM ZIP_LIKE ZL WHERE ZL.ZIP_ID = ZB.ZIP_ID) AS LIKE_COUNT FROM ZIP_BOARD ZB JOIN USERS U ON ZB.USERS_ID = U.USERS_ID
AND ZB.ZIP_PET_CATEGORY = #{cond.category}
AND (
U.USERS_ID LIKE '%' || #{cond.keyword} || '%'
ZB.ZIP_TITLE LIKE '%' || #{cond.keyword} || '%'
ZB.ZIP_CONTENT LIKE '%' || #{cond.keyword} || '%'
)
ORDER BY
LIKE_COUNT DESC
ZIP_VIEWCOUNT DESC
ZIP_UPDATE_AT DESC
) WHERE ROWNUM <= #{page.page} * #{page.size} ) WHERE RNUM > (#{page.page} - 1) * #{page.size}
